var view = require('../');

var _view;

function presentView () {
  _view.index.present({ time: new Date(), useLayout: false }, function (err, result) {
    console.log(err, result);
    setTimeout(function(){
      presentView();
    }, 100);
  });
};

view.create( { path: __dirname + "/../test/view20" } , function (err, v) {
  _view = v;
  console.log('cached', _view.index.presenter.cache);
  presentView();
});